var isHeroku = (process.env.DYNO) ? true : false;
var request = require('request');
var moment = require('moment');

if (!isHeroku){
    //local .env
    require('dotenv').config({silent: true});
}

var env = require('get-env')({
    development : ['dev', 'development'],
    production : ['prod', 'production'],
    test : ['test']
});

module.exports = {
    env : env,
    isHeroku : isHeroku,
    development: env === 'development',
    production : env === 'production',
    test : env === 'test',
    devOrTest : (env === 'development' || env === 'test'),
    requestUrl: function(url, callback){
        var timeoutInMilliseconds = 10*1000;
        var opts = {
            url: url,
            timeout: timeoutInMilliseconds
        };

        request(opts, function (err, response, body) {
            if (err) {
                console.log("Error requesting " + url + ": " + err);
                if (err){
                    callback(err, null);
                }
            }
            else{
                var json = JSON.parse(body);
                callback(null, json);
            }
        });
    },
    notificationTimeFromDate : function(date, convertToUtc){
        var momentDate = moment(date);
        if (convertToUtc){
            momentDate = momentDate.utc();
        }
        return momentDate.format("h:mm A").toLowerCase();
    },
    loopThroughObject : function(obj, forLoop){
        if (typeof obj === 'object'){
            for (var key in obj) {
                if (!obj.hasOwnProperty(key)) {
                    continue;
                }

                forLoop(key, obj[key]);
            }
        }
    },
    INTERVAL : 15
};